import RandomNumber from "components/RandomNumber";
import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [winner, setWinner] = useState('');
  const [dateState, setDateState] = useState(new Date());
  useEffect(() => {
    setInterval(() => {
      setDateState(new Date());
    }, 0);
  }, []);

  useEffect(() => {
    const testeDate = new Date(2022, 11, 2, 12, 45, 40, 0);
    prize(testeDate);
  }, [dateState]);

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });

  const currentTime = dateState.toLocaleString("pt-BR", {
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
    hour12: true,
  });

  const prize = (testeDate: Date) => {
    console.log("TESTE DATA" + testeDate);
    console.log("DATE STATE" + dateState);
    if (testeDate.getTime() === dateState.getTime()) {
      const winner = RandomNumber();
      setWinner(winner);
      alert(winner);
    }
    return "";
  };

  return (
    <>
      <p className="date-custom">{currentDate}</p>
      <p className="time-custom">{currentTime}</p>

      <div>
        {/* <textarea
          id="show-winner"
          className="custom-textarea"
          disabled
        ></textarea> */}
        {winner}
      </div>
    </>
  );
};

export default Datetime;
