import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [dateState, setDateState] = useState(new Date());
  useEffect(() => {
    setInterval(() => setDateState(new Date()), 0);
  }, []);

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "short",
    year: "numeric",
  });
  return (
    <>
      <p className="testando">{currentDate}</p>

      <p>
        {dateState.toLocaleString("pt-BR", {
          hour: "numeric",
          minute: "numeric",
          second: "numeric",
          hour12: true,
        })}
      </p>
    </>
  );
};

export default Datetime;
