import { useEffect, useState } from "react";

const Datetime = () => {
  const showDate = new Date();
  const displayTodaysDate =
    showDate.getDate() +
    "/" +
    showDate.getMonth() +
    "/" +
    showDate.getFullYear();
  const dt = showDate.toDateString();

  const displayTime =
    showDate.getHours() +
    ":" +
    showDate.getMinutes() +
    ":" +
    showDate.getSeconds();

  const [date, setDate] = useState('');

  const newDate = () => {
    setDate(displayTime);
  };

  useEffect(() => newDate());
  return (
    <>
      <div>
        {dt}
        <input type="text" value={displayTodaysDate} readOnly></input>
        {date}
      </div>
    </>
  );
};

export default Datetime;
