import RandomNumber from "components/RandomNumber";
import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [winner, setWinner] = useState("");
  const [dateState, setDateState] = useState(new Date());

  let timePrize = "16:24:00";
  const datePrize = "11/12/2022";
  console.log(dateState.toLocaleTimeString());
  console.log(dateState.toLocaleDateString());

  useEffect(() => {
    var timer = setInterval(() => setDateState(new Date()), 1000);
    return function cleanup() {
      clearInterval(timer);
    };
  });

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });

  const currentTime = dateState.toLocaleString("pt-BR", {
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
    hour12: true,
  });

  if (
    datePrize === dateState.toLocaleDateString() &&
    timePrize === dateState.toLocaleTimeString()
  ) {
    timePrize = '0';
    const winner = RandomNumber();
    setWinner(winner);
  }
  const teste = () => {
    setWinner(RandomNumber())
  }
  return (
    <>
      <p className="date-custom">{currentDate}</p>
      <p className="time-custom">{currentTime}</p>

      <div>
        {winner}
        <button onClick={teste}>Clique aqui</button>
      </div>
    </>
  );
};

export default Datetime;
