import RandomNumber from "components/RandomNumber";
import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [dateState, setDateState] = useState(new Date());
  useEffect(() => {
    setInterval(() => setDateState(new Date()), 0);
  }, []);

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });

  const currentTime = dateState.toLocaleString("pt-BR", {
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
    hour12: true,
  });

  const prize = () => {
    const testeDate = new Date(2022, 11, 2, 11, 46, 30);

    if (testeDate.getTime() === dateState.getTime()) {
      return alert("teste");
    }
    return "";
  };

  const winner = prize();

  return (
    <>
      <p className="date-custom">{currentDate}</p>
      <p className="time-custom">{currentTime}</p>

      <div>
        {/* <textarea
          id="show-winner"
          className="custom-textarea"
          disabled
        ></textarea> */}
        {winner}
      </div>
    </>
  );
};

export default Datetime;
