import RandomNumber from "components/RandomNumber";
import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [dateState, setDateState] = useState(new Date());
//   useEffect(() => {
//     setInterval(() => setDateState(new Date()), 10000000);
//   }, []);

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });

  const currentTime = dateState.toLocaleString("pt-BR", {
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
    hour12: true,
  });

  const prize = () => {
    if (
      currentDate === "2 de dezembro de 2022" &&
      currentTime === "11:23:25 AM"
    ) {
      console.log('teste');
      const winner = RandomNumber();
      return winner || "";
    }
    return "";
  };

//   const winner = setInterval(() => prize(), 1000);

  return (
    <>
      <p className="date-custom">{currentDate}</p>
      <p className="time-custom">{currentTime}</p>

      <div>
        {/* <textarea
          id="show-winner"
          className="custom-textarea"
          disabled
        ></textarea> */}

      </div>
    </>
  );
};

export default Datetime;
