import RandomNumber from "components/RandomNumber";
import { useEffect, useState } from "react";
import "./styles.css";

const Datetime = () => {
  const [winner, setWinner] = useState('');
  const [dateState, setDateState] = useState(new Date());
  const [prizeDateState, setPrizeDateState] = useState(new Date());

  let datePrize = '11/12/2022 15:25:07';
  console.log(dateState.toLocaleString())

  useEffect(() => {
    var timer = setInterval(() => setDateState(new Date()), 1000);
    return function cleanup() {
      clearInterval(timer);
    };
  });

  const currentDate = dateState.toLocaleDateString("pt-BR", {
    day: "numeric",
    month: "long",
    year: "numeric",
  });

  const currentTime = dateState.toLocaleString("pt-BR", {
    hour: "numeric",
    minute: "numeric",
    second: "numeric",
    hour12: true,
  });

  const prize = () => {
    if (datePrize === dateState.toLocaleString()) {
      const winner = RandomNumber();
      setWinner(winner);
      setDateState(new Date());
    }
  };

  prize();

  return (
    <>
      <p className="date-custom">{currentDate}</p>
      <p className="time-custom">{currentTime}</p>

      <div>
        {/* <textarea
          id="show-winner"
          className="custom-textarea"
          disabled
        ></textarea> */}
        {winner}
      </div>
    </>
  );
};

export default Datetime;
